package com.selim_jnu.kothayacho;import static android.content.Intent.FLAG_ACTIVITY_CLEAR_TASK;import android.content.Intent;import android.os.Bundle;import androidx.appcompat.app.AppCompatActivity;import com.google.android.gms.common.api.GoogleApiClient;import com.google.android.gms.location.LocationServices;import com.google.firebase.auth.FirebaseAuth;import java.util.Timer;import java.util.TimerTask;public class Splash extends AppCompatActivity {    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_splash);        new Timer().schedule(new TimerTask() {            @Override            public void run() {                if (SharedPrefs.getString(getApplicationContext(), "number").length() != 11) {                    startActivity(new Intent(Splash.this, SignInActivity.class));                } else {                    startActivity(new Intent(Splash.this, MainActivity.class).setFlags(FLAG_ACTIVITY_CLEAR_TASK));                    finish();                }            }        }, 1200);    }}