package com.selim_jnu.kothayacho.fragment;import android.annotation.SuppressLint;import android.os.Build;import android.os.Bundle;import android.util.Log;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ProgressBar;import android.widget.TextView;import android.widget.Toast;import androidx.annotation.NonNull;import androidx.annotation.Nullable;import androidx.annotation.RequiresApi;import androidx.fragment.app.Fragment;import androidx.fragment.app.FragmentManager;import androidx.recyclerview.widget.GridLayoutManager;import androidx.recyclerview.widget.LinearLayoutManager;import androidx.recyclerview.widget.RecyclerView;import com.google.firebase.database.ChildEventListener;import com.google.firebase.database.DataSnapshot;import com.google.firebase.database.DatabaseError;import com.google.firebase.database.DatabaseReference;import com.google.firebase.database.FirebaseDatabase;import com.google.firebase.database.ValueEventListener;import com.selim_jnu.kothayacho.Address;import com.selim_jnu.kothayacho.R;import com.selim_jnu.kothayacho.User;import com.selim_jnu.kothayacho.adapter.AddressAdapter;import java.util.ArrayList;import java.util.List;import java.util.Objects;public class StatusFragment extends Fragment {    List<Address> addresses;    public StatusFragment(List<Address> addresses) {        this.addresses = addresses;    }    @Override    public void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);    }    @Override    public View onCreateView(LayoutInflater inflater, ViewGroup container,                             Bundle savedInstanceState) {        return inflater.inflate(R.layout.fragment_status, container, false);    }    @SuppressLint("NotifyDataSetChanged")    @RequiresApi(api = Build.VERSION_CODES.R)    @Override    public void onViewCreated(@NonNull View view, @Nullable Bundle savedInstanceState) {        super.onViewCreated(view, savedInstanceState);        RecyclerView userView = view.findViewById(R.id.recyclerView);        AddressAdapter addressAdapter = new AddressAdapter(getActivity(),addresses);        userView.setLayoutManager(new LinearLayoutManager(getActivity()));        userView.setAdapter(addressAdapter);        addressAdapter.notifyDataSetChanged();    }}